template:
{% if args.city%}

{% if args.city == 'Pan India'%}
{% set activities = frappe.db.sql(""" select a.activity_name, a.city, a.description, a.start_date, a.status, m.user from `tabVol Activity` a left outer  join `tabVolunteer Mapping` as m on a.city = m.city and m.user = %(user)s where (a.city = '' or a.city is null) and status = %(status)s order by a.start_date desc""", {'status': 'Open', 'user': frappe.session.user}, as_dict=True )%}
{% else %}
{% set activities = frappe.db.sql(""" select a.activity_name, a.city, a.description, a.start_date, a.status, m.user from `tabVol Activity` a left outer  join `tabVolunteer Mapping` as m on a.city = m.city and m.user = %(user)s where a.city = %(city)s and status = %(status)s order by a.start_date desc""", {'status': 'Open', 'user': frappe.session.user, "city": args.city}, as_dict=True )%}
{% endif %}
{% else %}
{% set activities = frappe.db.sql(""" select a.activity_name, a.city, a.description, a.start_date, a.status, m.user from `tabVol Activity` a left outer  join `tabVolunteer Mapping` as m on a.city = m.city and m.user = %(user)s where (a.city is null or a.city = m.city or a.city = '') and status = %(status)s order by a.start_date desc""", {'status': 'Open', 'user': frappe.session.user}, as_dict=True )%}
{% endif %}

<h2 class="section-title">{{ title }}</h2>

<div class="row mt-4">
{% for activity in activities %}
<div class="col-md-4  " id="conference">
    <div class="mb-2">
  <div class="card-body">
      {% if activity.city %}
      <span class="city">{{ activity.city }}</span>
      {%else%}
      <span class="city">Pan India</span>
      {% endif %}
    <h5 class="card-title">{{activity.activity_name}}</h5>
    <p class="card-text">{{activity.description}}</p>
  </div>

  <ul class="list-group list-group-flush">
    <li class="list-group-item date">Start: {{ frappe.format_date(activity.start_date) }}</li>
  </ul>
  <div class="card-body">
    <a href="/activity/{{activity.activity_name}}" class="btn btn-primary">MORE DETAILS</a>
  </div>
  </div>
</div>
{% endfor %}
</div>

{% if args.city%}
{% if args.city == 'Pan India'%}
{% set activities = frappe.db.sql(""" select a.activity_name, a.city, a.description, a.start_date, a.status, m.user from `tabVol Activity` a left  join `tabVolunteer Mapping` as m on a.city = m.city and m.user = %(user)s where (a.city = '' or a.city is null) and status = %(status)s order by a.start_date desc""", {'status': 'Closed', 'user': frappe.session.user}, as_dict=True )%}

{% else %}
{% set activities = frappe.db.sql(""" select a.activity_name, a.city, a.description, a.start_date, a.status, m.user from `tabVol Activity` a left  join `tabVolunteer Mapping` as m on a.city = m.city and m.user = %(user)s where a.city = %(city)s  and status = %(status)s order by a.start_date desc""", {'status': 'Closed', 'user': frappe.session.user, "city": args.city}, as_dict=True )%}

{% endif %}
{% else %}
{% set activities = frappe.db.sql(""" select a.activity_name, a.city, a.description, a.start_date, a.status, m.user from `tabVol Activity` a left  join `tabVolunteer Mapping` as m on a.city = m.city and m.user = %(user)s where (a.city = m.city or a.city is null or a.city = '') and status ='Closed' order by a.start_date desc""", {'status': 'Closed', 'user': frappe.session.user}, as_dict=True )%}
{% endif %}


<h2 class="section-title">{{ title }}</h2>

<div class="row mt-4">
{% for activity in activities %}
<div class="col-md-4  " id="conference">
    <div class="mb-2">
  <div class="card-body">
      {% if activity.city %}
      <span class="city">{{ activity.city }}</span>
      {%else%}
      <span class="city">Pan India</span>
      {% endif %}
    <h5 class="card-title">{{activity.activity_name}}</h5>
    <p class="card-text">{{activity.description}}</p>
  </div>

  <ul class="list-group list-group-flush">
    <li class="list-group-item date">Start: {{ frappe.format_date(activity.start_date) }}</li>
  </ul>
  <div class="card-body">
    <a href="/activity/{{activity.activity_name}}" class="btn btn-primary">MORE DETAILS</a>
  </div>
  </div>
</div>
{% endfor %}
</div>

{% if args.city == 'Pan India'%}
{% set activity = frappe.db.sql("""select a.city, SEC_TO_TIME(SUM(TIME_TO_SEC(c.hours_spent))) as checkin_count from `tabVol Activity` a inner  join `tabVol Checkin` as c on a.name = c.activity where (a.city = '' or a.city is null) order by a.start_date desc""", as_dict=True )%}
{% set volunteers = frappe.db.sql("""SELECT c.user from `tabVol Checkin` c inner join `tabVol Activity` as a on c.activity = a.name where (a.city = '' or a.city is null) group by c.user""")%}
{% else %}
{% set activity = frappe.db.sql(""" select a.city, SEC_TO_TIME(SUM(TIME_TO_SEC(c.hours_spent))) as checkin_count from `tabVol Activity` a inner  join `tabVol Checkin` as c on a.name = c.activity  where a.city = %(city)s  order by a.start_date desc""", {"city": args.city}, as_dict=True )%}
{% set volunteers = frappe.db.sql("""SELECT c.user from `tabVol Checkin` c inner join `tabVol Activity` as a on c.activity = a.name where a.city = %(city)s group by c.user""", {"city": args.city})%}
{% endif %}

<div class="container">
    <div class="section-markdown">
        <div>
            <h3  class="activity-title">{%if activity[0].city %}{{activity[0].city}} {%else %}Pan India{%endif%}</h3>
            <p class="activity-desc">{{ activity[0].checkin_count }}</p>
        </div>
        <div class="row activity margin-top">
            <div class="col-md-4" >
                Total volunteers
                <p>
                    {{ volunteers|length }}
                </p>
            </div>
            <div class="col-md-4 ">
                Total volunteer hours
                <p>
                    {{ activity[0].checkin_count }}
                </p>
            </div>
        </div>
    </div>
</div>
